changeSet.createProcedure:
CREATE OR REPLACE FUNCTION ${tenant_id}_mod_fqm_manager.jsonb_array_elements(input jsonb)
RETURNS SETOF jsonb
AS $$
BEGIN
  RETURN QUERY SELECT * FROM jsonb_array_elements(input);
END;
$$ LANGUAGE 'plpgsql';

CREATE OR REPLACE FUNCTION ${tenant_id}_mod_fqm_manager.safe_uuid(val text)
RETURNS uuid
LANGUAGE plpgsql
IMMUTABLE
STRICT
AS $$
BEGIN
  RETURN val::uuid;
EXCEPTION WHEN invalid_text_representation THEN
  RETURN NULL;
END;
$$;
